svg.script:

const messages = [
  "Hey there! Welcome to Wild Kernels! ðŸ‘‹",
  "Building kernels and more for Android! ðŸ”§", 
  "TheWildJames was not The Impostor! à¶ž"
];

let messageIndex = 0;
let charIndex = 0;
let isTyping = true;

const typewriterElement = document.getElementById('typewriter');
const cursorElement = document.getElementById('cursor');

function typeWriter() {
  const currentMessage = messages[messageIndex];

  if (isTyping) {
    if (charIndex < currentMessage.length) {
      typewriterElement.textContent = currentMessage.substring(0, charIndex + 1);

      // Update cursor position
      const bbox = typewriterElement.getBBox();
      cursorElement.setAttribute('x', bbox.x + bbox.width + 5);

      charIndex++;
      setTimeout(typeWriter, 100); // Typing speed
    } else {
      // Finished typing, wait then start erasing
      isTyping = false;
      setTimeout(typeWriter, 2000); // Pause before erasing
    }
  } else {
    if (charIndex > 0) {
      typewriterElement.textContent = currentMessage.substring(0, charIndex - 1);

      // Update cursor position
      const bbox = typewriterElement.getBBox();
      cursorElement.setAttribute('x', bbox.x + bbox.width + 5);

      charIndex--;
      setTimeout(typeWriter, 50); // Erasing speed
    } else {
      // Finished erasing, move to next message
      isTyping = true;
      messageIndex = (messageIndex + 1) % messages.length;
      setTimeout(typeWriter, 500); // Pause before next message
    }
  }
}

// Start the animation
typeWriter();
